mapper.select:
select member_seq, member_id, member_pw, token
from user
where member_id=#{userId} and member_pw=#{password}

select member_seq, member_id
from user
where member_id = #{userId}

mapper.update:
update user
set token = #{token}
where member_id = #{userId}

mapper.select:
select token
from user
where member_id = #{userId}

mapper.update:
update user
set token = #{token, jdbcType=VARCHAR}
where member_id = #{userId}

update user
SET member_pw=#{password}
where member_id= #{userId};
